import { Switch, Route, NavLink } from "react-router-dom";
import Movie from "./components/Movie";
import FavMovie from "./components/FavMovie";
import { useDispatch, useSelector } from "react-redux";
import { addList, nextMovie, prevMovie, removeList } from "./actions/action";

function App() {
  const favMovies = useSelector(store => store.favMovies);
  const sira = useSelector(store => store.sira);
  const movies = useSelector(store => store.movies);
  const dispatch = useDispatch();



  function handleAddList() {
    dispatch(addList())
  };

  function handleNextMovie() {
    dispatch(nextMovie())
  };
  function handlePrevMovie() {
    dispatch(prevMovie())
  };
  function handleRemoveList() {
    dispatch(removeList(movies[sira].id))
  }

  return (
    <div className="wrapper max-w-2xl mx-auto">
      <nav className="flex text-2xl pb-6 pt-8 gap-2 justify-center">
        <NavLink to="/" exact className="py-3 px-6 " activeClassName="bg-white shadow-sm text-blue-600">
          Filmler
        </NavLink>
        <NavLink to="/listem" className="py-3 px-6 " activeClassName="bg-white shadow-sm text-blue-600">
          Listem
        </NavLink>
      </nav>
      <Switch>
        <Route exact path="/">
          <Movie sira={sira} />

          <div className="flex gap-3 justify-end py-3">
            <button

              className="select-none px-4 py-2 border border-blue-700 text-blue-700 hover:border-blue-500 hover:text-blue-500" onClick={handlePrevMovie}
            >
              Önceki
            </button>
            <button
              className="select-none px-4 py-2 border border-blue-700 text-blue-700 hover:border-blue-500 hover:text-blue-500"
              onClick={handleNextMovie}>
              Sıradaki
            </button>
            {!favMovies.inCludes(movies[sira]) ? (
              <button className="select-none px-4 py-2 bg-blue-700 hover:bg-red-600 text-white" onClick={handleAddList}
              >
                Listeme Ekle
              </button>
            ) : (
              <button className="select-none px-4 py-2 bg-blue-700 hover:bg-red-600 text-white" onClick={handleRemoveList}
              >
                Listemden Çıkar
              </button>
            )}
          </div>
        </Route>

        <Route path="/listem">
          <div>
            {favMovies.map((movie) => (
              <FavMovie key={movie.id} title={movie.title} id={movie.id} />
            ))}
          </div>
        </Route>
      </Switch>
    </div>
  );
}

export default App;
